    @Override
    public Firma findByIdImagen(Long idImagen) {

        Firma firma=new Firma();

        if(idImagen==1845343){
            firma.setIdFirma(1L);
            firma.setImagen("");
            firma.setIdTipoImagen(1);
            firma.setLongitud(1234);
        }
        if(idImagen==1845344){
            firma.setIdFirma(2L);
            firma.setImagen("");
            firma.setIdTipoImagen(1);
            firma.setLongitud(1618);
        }


        return firma;
    }
